import React, { useState } from "react";

export default function Dropdownmf() {
  const data = [
    {
      name: "Vegetables",
      items: ["Bendi", "LadyFingers", "Bengan"],
    },
    {
      name: "Vehicles",
      items: ["Pulser", "Shift"],
    },
    {
      name: "Courses",
      items: ["Java", "React js", "Node js"],
    },
  ];

  const [info, setinfo] = useState("");
  const [veg, setveg] = useState("");
  const [vehicle, setvehicle] = useState("");
  const [course, setcourse] = useState("");

  const onpump = (e) => {
    const loadinfo = data.find((s) => s.name === e.target.value).name;

    console.log("The loaded values are " + loadinfo);
    /*{
      loadinfo && loadinfo === "Vegetables";
    }*/
    setinfo(loadinfo);
  };

  if (info === "Vegetables"){ setveg(info);
  console.log("The vegetables values are " + veg);}

  return (
    <div>
      <div>
        <select name="stores" onChange={onpump}>
          <option>Vegetables</option>
          <option>Vehicles</option>
          <option>Courses</option>
        </select>
      </div>
      <div>
        <select name="vegetables">
          <option>Bendi</option>
          <option>Ladyfingers</option>
        </select>
        <select name="Vehicles">
          <option>PULSER</option>
          <option>SWIFT</option>
        </select>
        <select name="Technology">
          <option>Java</option>
          <option>React</option>
        </select>
      </div>
    </div>
  );
}
