import React, { useState } from "react";

export default function DropdownMapFilter() {
  const [country, setcountry] = useState("");
  const [cityval, setcity] = useState();

  const [info, setinfo] = useState("");

  const setloadd = (e) => {
    const countries = data.find((s) => s.country === e.target.value);
    setcity(countries.city);
  };

  const data = [
    { country: "India", city: ["Mumbai", "Delhi", "Pune"] },
    {
      country: "America",
      city: ["SanFrancisco", "denvar", "NewYork"],
    },
    {
      country: "ENGLAND",
      city: ["London", "Nottingham", "Derby"],
    },
  ];
  return (
    <div>
      <select name="con" value={info} onChange={(e) => setloadd()}>
        {data.map((result) => (
          <option>{result.country}</option>
        ))}
      </select>
      <div>
        <select name="cities" value={city}>
          {cityval.ma.map((res) => (
            <option>{res.city}</option>
          ))}
        </select>
      </div>
    </div>
  );
}
