    console.log("All counties list " + countries);
    //setcity(countries.city);
  };

  const data = [
    { country: "India", city: ["Mumbai", "Delhi", "Pune"] },
    {
      country: "America",
      city: ["SanFrancisco", "denvar", "NewYork"],
    },
    {
      country: "ENGLAND",
      city: ["London", "Nottingham", "Derby"],
    },
